package common

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var (
	exampleJWSTransactionA = JWSTransaction("..nUSBjL3Ap_vOzoOlr98qILmtKgwX2PVEZkBVLq3AlXTNUJm3uRXWcW99g2PSgWn095HzK3MQyZiwxRo5zyCDjg")
)

func TestJWSTransactionDecode(t *testing.T) {
	t.Run("exampleJWSTransactionA", func(t *testing.T) {
		decoded, err := exampleJWSTransactionA.Decode()
		if err != nil {
			t.Fatalf("expected no error, got %v", err)
		}

		assert.Equal(t, "com.example.app", decoded.BundleId)
		assert.Equal(t, "11111122223333", decoded.TransactionId)
		assert.Equal(t, "11111122223333", decoded.OriginalTransactionId)
		assert.Equal(t, "com.example.app.coolstuff", decoded.ProductId)
		assert.Equal(t, TransactionTypeConsumable, decoded.Type)
		assert.Equal(t, "AUD", decoded.Currency)
		assert.Equal(t, Price(59990), decoded.Price)
	})
}
